import React, { useEffect } from 'react';
import logo from '../assets/img/logo.png';  // Ajusta la ruta según tu estructura

const Navbar = () => {
  useEffect(() => {
    // Activar Bootstrap ScrollSpy en el navbar
    const mainNav = document.body.querySelector('#mainNav');
    if (mainNav) {
      new bootstrap.ScrollSpy(document.body, {
        target: '#mainNav',
        offset: 74,
      });
    }

    // Colapsar el navbar cuando un enlace de navegación se haga clic
    const navbarToggler = document.body.querySelector('.navbar-toggler');
    const responsiveNavItems = [].slice.call(
      document.querySelectorAll('#navbarResponsive .nav-link')
    );
    responsiveNavItems.map(function (responsiveNavItem) {
      responsiveNavItem.addEventListener('click', () => {
        if (window.getComputedStyle(navbarToggler).display !== 'none') {
          navbarToggler.click();
        }
      });
    });

    // Cleanup: eliminar los event listeners cuando el componente se desmonte
    return () => {
      responsiveNavItems.map(function (responsiveNavItem) {
        responsiveNavItem.removeEventListener('click', () => {
          if (window.getComputedStyle(navbarToggler).display !== 'none') {
            navbarToggler.click();
          }
        });
      });
    };
  }, []);  // El array vacío asegura que el efecto se ejecute solo una vez después del primer render

  return (
    <nav className="navbar navbar-expand-lg navbar-light fixed-top shadow-sm" id="mainNav">
      <div className="container px-5 d-flex justify-content-between align-items-center">
        <a className="navbar-brand fw-bold mx-auto order-1 order-lg-0" href="#page-top">
          <img src={logo} alt="Ever3st Group" style={{ width: '180px', height: 'auto' }} />
        </a>
        <button
          className="navbar-toggler order-2"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarResponsive"
          aria-controls="navbarResponsive"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span>Menu</span>
          <i className="bi bi-list"></i>
        </button>
        <div className="collapse navbar-collapse order-lg-1" id="navbarResponsive">
          <ul className="navbar-nav ms-auto me-4 my-3 my-lg-0" style={{ fontWeight: 'bolder' }}>
            <li className="nav-item">
              <a className="nav-link me-lg-3" href="#home">
                Inicio
              </a>
            </li>
            <li className="nav-item">
              <a className="nav-link me-lg-3" href="#testimonials">
                Testimonios
              </a>
            </li>
            <li className="nav-item">
              <a className="nav-link me-lg-3" href="#services">
                Servicios
              </a>
            </li>
            <li className="nav-item">
              <a className="nav-link me-lg-3" href="#pricing">
                Planes
              </a>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
